import React, { useState } from 'react';
import './App.css';

function App() {
  const [selectedFile, setSelectedFile] = useState(null);
  const [prediction, setPrediction] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleFileChange = (event) => {
    setSelectedFile(event.target.files[0]);
    setErrorMessage(''); // Clear error message on file selection
  };

  const handleUpload = async () => {
    if (!selectedFile) {
      alert("Please select a file before uploading.");
      return;
    }

    const formData = new FormData();
    formData.append('file', selectedFile);

    try {
      const response = await fetch('http://localhost:5000/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        throw new Error(`HTTP error! Status: ${response.status}`);
      }

      const data = await response.json();
      setPrediction(data.prediction); // Update state with the prediction
    } catch (error) {
      console.error('Error:', error);
      setErrorMessage('Failed to fetch prediction. Please try again.');
    }
  };

  return (
    <div className="App">
      <h1>File Upload for Prediction</h1>
      <input
        type="file"
        onChange={handleFileChange}
        className="file-input"
      />
      <button onClick={handleUpload} className="upload-button">Upload</button>
      {prediction && <h2 className="prediction">Predicted: {prediction}</h2>}
      {errorMessage && <h2 className="error-message">{errorMessage}</h2>}
    </div>
  );
}

export default App;
